create table food_item(id int auto_increment, name varchar(30) not null, description varchar(70), img_link blob, price DECIMAL(6,3) not null, item_availability varchar(40), item_type_id int not null, added_by int not null, added_at datetime not null, foreign key(item_type_id) references item_type(id), foreign key(added_by) references users(id), primary key(id, item_availability));